import React from "react";
import axios from "axios";
import { useState } from "react";

const Login = () => {
  const [mdp, setMdp] = useState("");
  const [pseudo, setPseudo] = useState("");

  const handlepseudo = (e) => {
    const pseudo_connexion = e.target.value;
    console.log(pseudo_connexion);
    setPseudo(pseudo_connexion);
  };

  const handlemdp = (e) => {
    const mdp = e.target.value;
    console.log(mdp);
    setMdp(mdp);
  };
  const submitConnexion = (e) => {
    e.preventDfault();
    const connexionData = { pseudo: pseudo, mdp: mdp };
    axios
      .post("http://localhost:5000/connexion", JSON.stringify(connexionData))
      .then(function (response) {
        // ! remplacer le "console.log" par un test de vérification d'un user et ajouter les pages utilisateurs
        console.log(response);
      })
      .catch(function (error) {
        console.log(error);
      });
  };
  return (
    <div className="login">
      <form onSubmit={submitConnexion}>
        <h2>Connexion</h2>
        <br /> <br />
        <label>Pseudo</label>
        <br />
        <input type="text" name="pseudo" onChange={(e) => handlepseudo(e)} />
        <br />
        <br />
        <label>Mot de passe</label>
        <br />
        <input type="password" name="mdp" onChange={(e) => handlemdp(e)} />
        <br />
        <br />
        <button type="submit">Connexion</button>
      </form>
    </div>
  );
};

export default Login;
