  axios
    .post("/connexion", {
      MDP: mdp,
      Pseudo: pseudo,
    })
    .then(function (response) {
      // ! remplacer le "console.log" par un test de vérification d'un user et ajouter les pages utilisateurs
      console.log(response);
    })
    .catch(function (error) {
      console.log(error);
    });

