import React from "react";
import axios from "axios";
import { useState } from "react";

const Login = () => {
  const [mdp, setMdp] = useState("");
  const [pseudo, setPseudo] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    const { pseudo } = e.target.elements.formpseudo;
    const { mdp } = e.target.elements.formmdp;
    setPseudo(pseudo);
    setMdp(mdp);
    axios.post(
      "http://localhost:5000/connexion",
      { pseudo: pseudo },
      { mdp: mdp }
    );
  };

  return (
    <div className="login">
      <form onSubmit={handleSubmit}>
        <h2>Connexion</h2>
        <br /> <br />
        <label>Pseudo</label>
        <br />
        <input type="text" name="formpseudo" />
        <br />
        <br />
        <label>Mot de passe</label>
        <br />
        <input type="password" name="formpmdp" />
        <br />
        <br />
        <button type="submit">Connexion</button>
      </form>
    </div>
  );
};

export default Login;
